    private Object addPlace(String userEmailRef, Object nto100){
        String name = "Place"+generateRandomNumber();
        String urlMap = "https://maps.app.goo.gl/v78yaSt1ni9pAyMNA";
        String workingHours = "понеделник - неделя: 9:00ч.–17:00ч.";
        String placePhoneNumber = "043162499"+generateRandomNumber();
        String imgPath = "data:image/jpeg;base64,";
        int distance=Integer.parseInt(generateRandomNumber());
        boolean isFavourite = false;
        boolean isVisited = true;
        final Object[] retreivedUser = {null};
        Place shipka = new Place(name, urlMap, workingHours, placePhoneNumber, imgPath, distance, isFavourite, isVisited, userEmailRef, nto100);
        firestore.collection("places").add(shipka)
                .addOnSuccessListener(new OnSuccessListener<DocumentReference>() {
                    @Override
                    public void onSuccess(DocumentReference documentReference) {
                        // Place successfully added to Firestore
                        Log.d("Firestore", "Place added successfully with ID: " + documentReference.getId());
                        retreivedUser[0] = documentReference;
                    }
                })
                .addOnFailureListener(new OnFailureListener() {
                    @Override
                    public void onFailure(@NonNull Exception e) {
                        // Failed to add place to Firestore
                        Log.e("Firestore", "Error adding place", e);
                    }
                });
        return retreivedUser[0];
    }
